sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function () {		
	},	
isDatePast: function(rn, wn){
		// Create GlideDateTime objects for the current date and the passed in date
		// If the testdate is before rightnow, return true, else return false
		if (wn.before(rn)) {
			return true;
		}
		else {
			return false;
		}
	},
isDateToday: function(rn, wn){
		var r = rn.getLocalDate();
		var w = wn.getLocalDate();
		// Create GlideDateTime objects for the current date and the passed in date		
		// Compare today and istoday to see if they are the same day
		//gs.addErrorMessage(today + "  |   " + istoday);
		if(r.compareTo(w) == 0){
			return true;
		}
		else {
			return false;
		}
	},
    type: 'NeedItUtils'
};
